mapper.update:
UPDATE MEMBER
SET STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_PWD, MEMBER_NAME, CREATE_DATE, GRADE_NO, POINT
FROM MEMBER
WHERE STATUS = 'Y' AND MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
   SET POINT = POINT-#{point}
 WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER
   SET GRADE_NO=#{gradeNo}
 WHERE MEMBER_NO = #{memberNo}
